
public class Ejercicio16 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
	int n1=250;
	byte n2=2;
	final byte n3=5;
	int n4=n1/n2;
	int n5=n4/5;	
	
	
	

		System.out.println("La variable \"n1\" entre la variable \"n2\" da como resultado: "+n1/n2);
		System.out.println("El resultado de la operación anterior entre 5 (literal) da como resultado: "+n4/5);
		System.out.println("Por ultimo, el resultado anterior entre la constante \"n3\" da como resultado: "+n5/n3);
		
					
	}

}
